    private void jButton28ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton28ActionPerformed
        try{
        num2= Double.parseDouble(jTextField2.getText());
        switch(operator)
        {
            case "+":
            {
                ans=num+num2;
                jTextField1.setText("");
                jTextField2.setText(Double.toString(ans));
                break;
            }
            case "-":
            {
                ans=num-num2;
                jTextField1.setText("");
                jTextField2.setText(Double.toString(ans));
                break;
            }
            case "/":
            {
                ans=num/num2;
                jTextField1.setText("");
                jTextField2.setText(Double.toString(ans));
                break;
            }
            case "*":
            {
                ans=num*num2;
                jTextField1.setText("");
                jTextField2.setText(Double.toString(ans));
                break;
            }
            case "%":
            {
                ans=num%num2;
                jTextField1.setText("");
                jTextField2.setText(Double.toString(ans));
                break;
            }
            default:
            {
                ans=Double.parseDouble(jTextField2.getText());
                jTextField1.setText("");
                jTextField2.setText(Double.toString(ans));
                break;
            }
            
        }
        operator="=";
        }
        catch(Exception e)
        {
            JOptionPane.showMessageDialog(null, "not possible ");
        }
    }//GEN-LAST:event_jButton28ActionPerformed

